package gcg

var operationsTemplateSrc = `package {{.Package}}
// generated by github.com/emicklei/graphql-client-gen/cmd/gcg version: {{.BuildVersion }}
// DO NOT EDIT

import (
	"time"
)

var (
	_ = time.Now
)

{{- range .Mutations}}

// {{.FunctionName}}Mutation is used for both specifying the query and capturing the response. {{.Comment}}
type {{.FunctionName}}Mutation struct {
	Errors Errors {{.ErrorsTag}}
	Data struct {
		{{.ReturnType}} {{.ReturnFieldTag}}
	} {{.DataTag}}
}

// Build returns a GraphQLRequest with all the parts to send the HTTP request.
func (_m {{.FunctionName}}Mutation) Build(
		{{- range .Arguments}}
		{{.Name}} {{.Type}}, 
		{{- end }}
	) GraphQLRequest { 
	_typedVars := map[string]valueAndType{
		{{- range .Arguments}}
		"{{.JSONName}}": {value:{{.Name}},graphType:"{{.GraphType}}"},
		{{- end }}
	}	
	_query, _vars := buildMutation("{{.Name}}", _m.Data, _typedVars)
	return GraphQLRequest{
		Query:         _query,
		OperationName: "{{.Name}}",
		Variables:     _vars,
	}
}

{{- end }}
`
